<?php
// config.php
session_start();

// Salvar configuraÃ§Ãµes de sensibilidade
if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $_SESSION['dpi'] = (int)$_POST['dpi'];
    $_SESSION['sens'] = (float)$_POST['sens'];
    $_SESSION['edpi'] = $_SESSION['dpi'] * $_SESSION['sens'];
}

// Calcular eDPI (effective DPI)
$edpi = $_SESSION['edpi'] ?? 400; // Valor padrÃ£o
?>

<!DOCTYPE html>
<html>
<head>
    <title>Valorant Aim Trainer</title>
    <style>
        body {
            background: #1a1a1a;
            color: white;
            font-family: Arial, sans-serif;
        }
        #arena {
            width: 800px;
            height: 600px;
            border: 2px solid #ff4655;
            position: relative;
            overflow: hidden;
            margin: 20px auto;
        }
        .target {
            width: 30px;
            height: 30px;
            background: #ff4655;
            border-radius: 50%;
            position: absolute;
            cursor: crosshair;
            transition: all 0.3s;
        }
        .stats {
            text-align: center;
            padding: 20px;
        }
    </style>
</head>
<body>
    <div class="stats">
        <h1>ðŸ¥· Treinador de Mira Valorant</h1>
        <form method="POST">
            <label>DPI do Mouse: 
                <input type="number" name="dpi" value="<?= $_SESSION['dpi'] ?? 800 ?>" required>
            </label>
            
            <label>Sensibilidade no Jogo: 
                <input type="number" step="0.1" name="sens" value="<?= $_SESSION['sens'] ?? 0.5 ?>" required>
            </label>
            
            <button type="submit">ðŸŽ¯ Aplicar ConfiguraÃ§Ãµes</button>
        </form>
        <p>eDPI Calculado: <?= $edpi ?> (DPI * Sensibilidade)</p>
        <p>Acertos: <span id="hits">0</span> | Erros: <span id="misses">0</span></p>
    </div>

    <div id="arena"></div>

    <script>
        // ConfiguraÃ§Ãµes baseadas no PHP
        const edpi = <?= $edpi ?>;
        let hits = 0;
        let misses = 0;

        // Fator de dificuldade baseado no eDPI
        const targetSpeed = Math.max(1000, 2000 - (edpi * 0.5));
        const targetSize = Math.max(20, 50 - (edpi * 0.02));

        function createTarget() {
            const target = document.createElement('div');
            target.className = 'target';
            
            // PosiÃ§Ã£o aleatÃ³ria
            const x = Math.random() * (800 - 30);
            const y = Math.random() * (600 - 30);
            
            target.style.left = `${x}px`;
            target.style.top = `${y}px`;
            
            // Tamanho dinÃ¢mico
            target.style.width = `${targetSize}px`;
            target.style.height = `${targetSize}px`;

            // Movimento aleatÃ³rio
            const moveInterval = setInterval(() => {
                const newX = Math.random() * (800 - 30);
                const newY = Math.random() * (600 - 30);
                target.style.transform = `translate(${newX - x}px, ${newY - y}px)`;
            }, targetSpeed);

            // Sistema de pontuaÃ§Ã£o
            target.addEventListener('click', () => {
                hits++;
                document.getElementById('hits').textContent = hits;
                target.remove();
                clearInterval(moveInterval);
                createTarget();
                playHitSound();
            });

            // Timeout para erro
            setTimeout(() => {
                if (target.parentElement) {
                    misses++;
                    document.getElementById('misses').textContent = misses;
                    target.remove();
                    createTarget();
                }
            }, 2000);

            document.getElementById('arena').appendChild(target);
        }

        function playHitSound() {
            const audio = new Audio('data:audio/wav;base64,');
            audio.play();
        }

        // Iniciar o treino
        for(let i = 0; i < 5; i++) {
            createTarget();
        }
    </script>
</body>
</html>